         "context": {
             "activityID": "3",
             "stepID": "2",
             "workflowID": "2"
         },
         "agent": {
             "role": "ETL",
             "ID": "UnifiedViews"
         },
         "activity": {
             "status": "success",
             "type": "StepExecution",
             "title": "uv-dpu-l-attx-uploader"
         }
     },
